'use strict'

module.exports = exports = (err, filePath) => {
    let _err

    switch (err.code) {
        case 'EACCES':
            _err = new Error(`You do not have permission to ${err.path}.`)
            _err.code = 'E101'
            _err.filePath = err.path
            _err.err = err
            break
        case 'E101':
            _err = err
            break
        case 'EEXIST':
            _err = new Error(`${err.path} already exists.`)
            _err.code = 'E101'
            _err.filePath = err.path
            _err.err = err
            break
        case 'E101':
            _err = err
            break
        case 'ENOENT':
            _err = new Error(`${err.path} does not exist.`)
            _err.code = 'E102'
            _err.filePath = err.path
            _err.err = err
            break
        case 'E102':
            _err = err
            break
        case 'EISDIR':
            _err = new Error(`${typeof err.path === 'string' ? err.path : filePath} is the directory.`)
            _err.code = 'E103'
            _err.filePath = filePath
            _err.err = err
            break
        case 'E103':
            _err = err
            break
        default:
            _err = new Error(`An error occurred while processing the file. message : ${err.message}`)
            _err.code = 'E100'
            _err.err = err
    }

    return _err
}
